import React, { Component } from 'react';
import { fetch_get } from './Fetch';
import { LOGIN } from './apiUrls'
import Context from '../Context';

class Signin extends Component {
  static contextType = Context;
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      password: '',
      errors: {},
      token: '',
    };
    this.handleChangeEmail = this.handleChangeEmail.bind(this);
    this.handleChangePassword = this.handleChangePassword.bind(this);
  }

  gotoDashboard() {
      window.location.href = '/dashboard';
  }

  handleChangeEmail(event) {
    this.setState({ username: event.target.value, error: '', errors: {} });
  }

  handleChangePassword(event) {
    this.setState({ password: event.target.value, error: '', errors: {} });
  }

  handleSubmit(event) {
    event.preventDefault();

    const { username, password } = this.state;
      let data = new FormData()
      data.append('username', username)
      data.append('password', password)
      fetch(LOGIN, {
        method: 'post',
        body: data
      })
      .then(response => response.json())
      .then((response) => {
        if (!response.error) {
          localStorage.setItem('token', response.token);
          localStorage.setItem('id', response.id);
          localStorage.setItem('username', response.username);
          window.location.href = '/dashboard';
        }
        else {
          this.setState({ errors: response.errors });
        }
      })
      //.catch((error) => {
        .catch(() => {
        this.setState({ error: 'Something went wrong, Please try again' });
      });
  }

  componentDidMount() {
    if(localStorage.getItem('token')) {
      fetch_get('/api/user-details/')
      .then((response) => {
        // this.context.setData(response)
      })
    }
    else {
      this.props.history.push('/signin')
    }
  }

  render() {
    const { errors } = this.state;
    return (
      <section className="login p-fixed d-flex text-center bg-primary common-img-bg">
        <div className="container">
            <div className="row">
                <div className="col-sm-12">
                    <div className="login-card card-block auth-body mr-auto ml-auto">
                        <form className="md-float-material">
                            <div className="text-center">
                                <img src="assets/images/logo.png" alt="logo.png" />
                            </div>
                            <div className="auth-box">
                            	<p className='error-text'>{errors.invalid ? errors.invalid : null}</p>
                                <div className="row m-b-20">
                                    <div className="col-md-12">
                                        <h3 className="text-left txt-primary">Sign In</h3>
                                    </div>
                                </div>
                                <hr/>
                                <div className="input-group">
                                    <input type="text" className="form-control" placeholder="UserName" value={this.state.email} onChange={this.handleChangeEmail} required />
                                    <span className="md-line"></span>
                                </div>
                                <p className='error-text'>{errors.username ? errors.username[0] : null}</p>
                                <div className="input-group">
                                    <input type="password" className="form-control" placeholder="Password" value={this.state.password} onChange={this.handleChangePassword} required />
                                    <span className="md-line"></span>
                                </div>
                                <p className='error-text'>{errors.password ? errors.password[0] : null}</p>
                                <div className="row m-t-25 text-left">
                                    <div className="col-sm-7 col-xs-12">
                                        <div className="checkbox-fade fade-in-primary">
                                            <label>
                                                <input type="checkbox" value="" />
                                                <span className="cr"><i className="cr-icon icofont icofont-ui-check txt-primary"></i></span>
                                                <span className="text-inverse">Remember me</span>
                                            </label>
                                        </div>
                                    </div>
                                    <div className="col-sm-5 col-xs-12 forgot-phone text-right">
                                        <a href="auth-reset-password.html" className="text-right f-w-600 text-inverse"> Forgot Your Password?</a>
                                    </div>
                                </div>
                                <div className="row m-t-30">
                                    <div className="col-md-12">
                                        <button type="button" className="btn btn-primary btn-md btn-block waves-effect text-center m-b-20" onClick={this.handleSubmit.bind(this)}>Sign in</button>
                                    </div>
                                </div>
                                <hr/>
                                <div className="row">
                                    <div className="col-md-10">
                                        <p className="text-inverse text-left m-b-0">Thank you and enjoy our website.</p>
                                        <p className="text-inverse text-left"><b>Your Authentication Team</b></p>
                                    </div>
                                    <div className="col-md-2">
                                        <img src="assets/images/auth/Logo-small-bottom.png" alt="small-logo.png" />
                                    </div>
                                </div>

                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    </section>
    );
  }
}
export default Signin;
