import React, { Component } from 'react';
import { DOMAIN_NAME } from './apiUrls'
import Context from '../Context';
import { Link } from 'react-router-dom';


export default class LeftMenu extends Component {
  static contextType = Context;

  constructor(props) {
    super(props);
    this.state = {
      selectedNav: window.location.pathname.split('/')[2],
      role: '',
      isNewBundle: false
    };
    this.isActive = this.isActive.bind(this);
    this.logout = this.logout.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  onClick(value) {
    this.setState({ selectedNav: value });
    this.props.history.push(`/${value}/`);
  }

  isActive(value) {
    return 'nav-link ' + ((value === this.state.selectedNav) ? ' active' : '');
  }

  logout() {
    localStorage.clear();
    this.props.history.push('/signin');
  }

  handleChange = (selectedMenuName) => {
    if(selectedMenuName){
      this.setState({ selectedNav : selectedMenuName.value});
      this.onClick(selectedMenuName.value);
    }
  }

  render() {
    const { selectedNav } = this.state;

    return (
    <div className="bg-light border-right" id="sidebar-wrapper">
      <div className="sidebar-heading">Start Bootstrap </div>
      <div className="list-group list-group-flush">
        <Link to={'/teams/list/'} className="list-group-item list-group-item-action bg-light">Teams</Link>
        <Link to={'/players/list/'} className="list-group-item list-group-item-action bg-light">Players</Link>
        <Link to={'/match/list/'} className="list-group-item list-group-item-action bg-light">Match</Link>
        <Link to={'/points-table/list/'} className="list-group-item list-group-item-action bg-light">PointsTable</Link>
      </div>
    </div>
    );
  }
}
