import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { fetch_get } from './Fetch';
import Context from '../Context';

class Dashboard extends Component {
  static contextType = Context;
  constructor(props) {
    super(props);
    this.state = {};
        this.logout = this.logout.bind(this);

  }

  componentDidMount() {
    if(localStorage.getItem('token')) {
      fetch_get('/api/user-details/')
      .then((response) => {
        // this.context.setData(response)
      })
    }
    else {
      this.props.history.push('/signin')
    }
  }

logout() {
    localStorage.clear();
      window.location.href = '/signin';

  }


  render() {
    const { permissions, role } = this.context;
    const {username} = this.state;
    return (
      <nav className="navbar header-navbar pcoded-header">
               <div className="navbar-wrapper">
                   {username}
                   <div className="navbar-container container-fluid">
                       <ul className="nav-left">
                           <li>
                               <div className="sidebar_toggle"><Link to={"#"}><i className="ti-menu"></i></Link></div>
                           </li>
                           <li className="header-search">
                               <div className="main-search morphsearch-search">
                                   <div className="input-group">
                                       <span className="input-group-addon search-close"><i className="ti-close"></i></span>
                                       <input type="text" className="form-control" />
                                       <span className="input-group-addon search-btn"><i className="ti-search"></i></span>
                                   </div>
                               </div>
                           </li>
                           <li>
                               <Link to={"#!"}>
                                   <i className="ti-fullscreen"></i>
                               </Link>
                           </li>
                       </ul>
                       <ul className="nav-right">


                           <li className="user-profile header-notification">
                               <Link to={"#!"}>
                                   <img src="/assets/images/default-user.jpeg" className="img-radius" alt="User-Profile-Image" />
                                   <span>{username}</span>
                                   <i className="ti-angle-down"></i>
                               </Link>
                               <ul className="show-notification profile-notification">
                                   <li>
                                       <Link onClick={this.logout.bind(this)}>
                                           <i className="ti-layout-sidebar-left"></i> Logout
                                       </Link>
                                   </li>
                               </ul>
                           </li>
                       </ul>
                   </div>
               </div>
           </nav>
    );
  }
}
export default Dashboard;
