              <div className="card-body">
                <h5 className="card-title text-center">{username}</h5>
                <Link onClick={this.logout.bind(this)}>
                                           <i className="ti-layout-sidebar-left"></i> Logout
                                       </Link>
            </div>
            </div>
            </div>
            </div>
